        </Form.Field>
        <b>.com</b>
      </Input.Group>
    </Form.Item>

    <Form.Item label='Friends' required>
      <Form.FieldSet
        name='friends'
        empty={(insert) => {
          return (
            <Button type='primary' mode='outline' onClick={() => insert({ name: '' })}>
              + Add new friends
