      </Input.Group>

      <Input.Group style={style}>
        <Input placeholder='onEnterPress' onEnterPress={onEnterPress} />
        <b className='onEnterPress'>{`onEnterPress: ${keyEnterPress} times`}</b>
      </Input.Group>
    </div>
  );
};

