import React from 'react';
import { Button, Input } from 'shineout';

export default () => {
  const [value, setValue] = React.useState();
  const onIncrease = () => {
    setValue((v) => {
      const next = Number(v || 0) + 1;
      return next;
    });
  }

  const [defaultValue, setDefaultValue] = React.useState(1);
