      <div style={{ marginBottom: 24 }}>
        <Avatar.Group max={3}>{renderAvatars()}</Avatar.Group>
      </div>
      <div>
        <Avatar.Group max={3} renderMax={renderMax}>
          {renderAvatars()}
        </Avatar.Group>
      </div>
    </div>
  );
};

