      <Input.Group width={240}>
        <Input style={{ flex: 3 }} value='formatResult' disabled />
        <Input
          style={{ flex: 4 }}
          placeholder='å±•ç¤ºæ ¼å¼åŒ–'
          value={formatResult}
          onChange={handleFormatResultChange}
        />
      </Input.Group>
    </div>
  );
};

export default App;

