import React from 'react';
import { Input, Gap, Link, icons } from 'shineout';

const btnStyle: React.CSSProperties = {
  cursor: 'pointer',
  padding: '0 8px',
  background: 'var(--soui-input-group-font-background-color,var(--soui-neutral-fill-2,#F4F5F8))',
