    setDefaultValue((v) => {
      const next = Number(v || 0) + 1;
      return next;
    });
  }
  return (
    <div style={{display: 'flex', gap: 12}}>
      <Input.Number
        width={300}
        placeholder='input something'
        defaultValue={defaultValue}
        type="number"
        // value={value}
