/**
 * cn - åˆ†é¡µ
 *    -- å‰ç«¯åˆ†é¡µçš„æƒ…å†µä¸‹, è®¾ç½® pagination æ˜¾ç¤ºåˆ†é¡µï¼Œæ²¡æœ‰è®¾ç½® onChange å¤„ç†æ•°æ®çš„æƒ…å†µä¸‹ï¼Œä¼šè‡ªåŠ¨å¯¹æ•°æ®è¿›è¡Œåˆ†é¡µ
 *    -- pagination çš„å‚æ•°å’Œ Pagination ç»„ä»¶ä¸€è‡´
 * en - Pagination
 *   -- Set the pagination property to show the pagination and if not set onChange property, the data is automatically paged
 *    -- The parameters of pagination are consistent with the Pagination component
 */
import React, { useState, useEffect } from 'react';
import { List, TYPE } from 'shineout';
import { user } from '@sheinx/mock';

interface ListItem {
  id: number;
  time: string;
  start: string;
  height: number;
  salary: number;
  office: string;
  country: string;
  office5: string;
  position: string;
  lastName: string;
  firstName: string;
}
type ListProps = TYPE.List.Props<ListItem, ListItem>;
type ListRenderItem = ListProps['renderItem'];

const App: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [current, setCurrent] = useState<number>(1);
  const [data, setData] = useState<ListItem[]>([]);

  const fetchData = (c: number) => {
    setLoading(true);
    user.fetch
      .get('List', { current, pageSize: 10, sorter: {}, username: '' })
      .then((_data: { data: any }) => {
        setData(_data.data);
        setLoading(false);
        setCurrent(c);
      });
  };

  useEffect(() => {
    fetchData(1);
  }, []);

  const renderItem: ListRenderItem = (rowData) => (
    <div>{`Name: ${rowData.firstName}-${rowData.lastName}`}</div>
  );

  const handlePageChange = (index: number) => {
    if (index > 10) return;
    fetchData(index);
  };

  const pagination = {
    current,
    total: 100,
    pageSize: 10,
    align: 'right',
    onChange: handlePageChange,
  };

  return (
    <List
      keygen='id'
      format='id'
      bordered
      data={data}
      loading={loading}
      renderItem={renderItem}
      pagination={pagination}
    />
  );
};

export default App;

/**
 * cn - é€‰æ‹©è¡Œ
 *    -- è®¾ç½® onChange å±æ€§ï¼Œä¼šè‡ªåŠ¨æ·»åŠ é€‰æ‹©è¡Œ
 * en - Select
 *    -- Set the onChange property will automatically add a row with checkbox
 */
import React, { useState, useEffect } from 'react';
import { List, Checkbox, TYPE } from 'shineout';
import { user } from '@sheinx/mock';

interface ListItem {
  id: number;
  time: string;
  start: string;
  height: number;
  salary: number;
  office: string;
  country: string;
  office5: string;
  position: string;
  lastName: string;
  firstName: string;
}
type ListProps = TYPE.List.Props<ListItem, number[]>;
type ListOnChange = ListProps['onChange'];
type ListRenderItem = ListProps['renderItem'];

type CheckboxProps = TYPE.Checkbox.Props<any>;
type CheckboxOnChange = CheckboxProps['onChange'];

const style: React.CSSProperties = {
  padding: '12px 16px',
  display: 'flex',
  fontSize: 14,
  lineHeight: '22px',
  alignItems: 'center',
  justifyContent: 'flex-start',
  border: '1px solid transparent',
};

const App: React.FC = () => {
  const [current, setCurrent] = useState(1);
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState<ListItem[]>([]);
  const [value, setValue] = useState<number[]>([1]);

  const fetchData = (c: number) => {
    setLoading(true);
    user.fetch
      .get('List', { current, pageSize: 10, sorter: {}, username: '' })
      .then((_data: { data: ListItem[] }) => {
        setData([...data, ..._data.data]);
        setCurrent(c);
        setLoading(false);
      });
  };

  const getChecked = () => {
    if (!value || value.length <= 0) return false;
    if (value.length === data.length) return true;
    return 'indeterminate';
  };

  const onChange: ListOnChange = (selectedValue, selectedDataItem, checked) => {
    console.log('selectValue, selectedDataItem, checked: ', selectedValue, selectedDataItem, checked);
    setValue(selectedValue);
  };

  const checkboxOnChange: CheckboxOnChange = (flag: boolean) => {
    if (flag) {
      setValue(data.map((v: ListItem) => v.id));
      return;
    }
    setValue([]);
  };

  const renderItem: ListRenderItem = (rowData) => (
    <div>{`From ${rowData.country}. Name: ${rowData.firstName}-${rowData.lastName}`}</div>
  );

  useEffect(() => {
    fetchData(1);
  }, []);

  return (
    <div>
      <div style={style}>
        <Checkbox checked={getChecked()} onChange={checkboxOnChange} />
        <div>{`Selected ${value.length}`}</div>
      </div>
      <List
        format='id'
        keygen='id'
        bordered
        data={data}
        value={value}
        loading={loading}
        onChange={onChange}
        renderItem={renderItem}
      />
    </div>
  );
};

export default App;

