          />
        </Popover>
      </span>
    );
  };

  return (
    <div>
      <Select
        width={300}
        data={data}
        keygen='id'
        multiple
        compressed
        value={value}
        onChange={handleChange}
        compressedBound={2}
        renderCompressed={renderCompressed}
        placeholder='Static lineHeight'
        renderItem={renderItem}
        clearable
      />
    </div>
  );
};

