    },
  ];

  return (
    <div>
      <Checkbox.Group
        style={{ marginBottom: 24 }}
        keygen='id'
        value={expandKeys}
        onChange={setExpandKeys}
      >
        {[1, 2, 3, 4, 5].map((d) => (
          <Checkbox key={d} htmlValue={d}>{`å±•å¼€ç¬¬ ${d} è¡Œ`}</Checkbox>
        ))}
      </Checkbox.Group>

      <Table
        expandKeys={expandKeys}
        data={data}
        keygen='id'
        style={{ height: 300 }}
        columns={columns}
      />
    </div>
  );
};

export default App;

