      title: 'operate',
      render: (_d, i) => (
        <div style={{ height: 100 }}>
          <button
            onClick={() =>
              setList((pre) => {
                const temp = [...pre];
                temp.splice(i, 1);
                return temp;
              })
            }
          >
            remove
          </button>
          <button onClick={() => {
            setList((pre) => {
              const temp = [...pre];
              temp.splice(i, 0, { a: Math.random().toString(32), id: Math.random() * 100000 });
              return temp;
            })
          }}>
            add row
          </button>
        </div>
      ),
    },
  ];

  return (
    <div
      style={{
        height: 1000,
        background: '#ccc',
        padding: 20,
        display: 'flex',
        flexDirection: 'column',
      }}
    >
      <div style={{ height: 100 }}></div>
