const url =
  'https://raw.githubusercontent.com/sheinsight/shineout-static/main/shineout-next/images/avatar';

const avatarImgIndex = [1, 2, 3, 4, 5, 6, 7, 8];

export default () => {
  const renderAvatars = () => {
