  return (
    <div>
      <Button onClick={show} mode="outline">click me</Button>
      <Drawer
        width={400}
        visible={visible}
        title='Drawer Title'
        onClose={handleCancel}
        footer={[
          <Button key='cancel' onClick={handleCancel} mode='outline'>
            Cancel
          </Button>,
          <Button key='ok' type='primary' onClick={handleOk}>
            Ok
          </Button>,
        ]}
      >
        {`you are visited ${content}`}
      </Drawer>
    </div>
  );
};

export default App;

