import React, { useState, useEffect } from 'react';
import { List, TYPE } from 'shineout';
import { user } from '@sheinx/mock';

interface ListItem {
  id: number;
  time: string;
  start: string;
  height: number;
  salary: number;
  office: string;
  country: string;
  office5: string;
  position: string;
  lastName: string;
  firstName: string;
}
type ListProps = TYPE.List.Props<ListItem, ListItem>;
type ListRenderItem = ListProps['renderItem'];

const App: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [current, setCurrent] = useState<number>(1);
  const [data, setData] = useState<ListItem[]>([]);

  const fetchData = (c: number) => {
    setLoading(true);
    user.fetch
      .get('List', { current, pageSize: 10, sorter: {}, username: '' })
      .then((_data: { data: any }) => {
        setData(_data.data);
        setLoading(false);
        setCurrent(c);
      });
  };

  useEffect(() => {
    fetchData(1);
  }, []);

  const renderItem: ListRenderItem = (rowData) => (
    <div>{`Name: ${rowData.firstName}-${rowData.lastName}`}</div>
  );

  const handlePageChange = (index: number) => {
    if (index > 10) return;
    fetchData(index);
  };

  const pagination = {
    current,
    total: 100,
    pageSize: 10,
    align: 'right',
    onChange: handlePageChange,
  };

  return (
    <List
      keygen='id'
      format='id'
      bordered
      data={data}
      loading={loading}
      renderItem={renderItem}
      pagination={pagination}
    />
  );
};

export default App;

