 *    -- The mode of the tag is divided into bright, fill, outline, brightOutline line 4 kinds
 */

import { Tag } from 'shineout';
export default () => {
  const TagColor = ['default', 'info', 'danger', 'warning', 'success'];
  const TagMode = ['bright', 'fill', 'outline', 'brightOutline'];

  const capitalizeFirstLetter = (str: string) => {
    return str.charAt(0).toUpperCase() + str.slice(1);
  };

  return (
    <div>
      {TagMode.map((mode, midx) => {
        return (
          <div key={midx} style={{ marginBottom: midx === TagMode.length - 1 ? 0 : 24 }}>
            {TagColor.map((color, cidx) => (
              <Tag mode={mode as any} key={cidx} color={color as any}>
                {capitalizeFirstLetter(color)}
              </Tag>
            ))}
          </div>
        );
      })}
    </div>
  );
};

/**
 * cn - å¤šè‰²æ ‡ç­¾
 *    -- é™¤å¸¸ç”¨ 5 ç§å¸¸ç”¨çš„åŸºæœ¬åŠŸèƒ½è‰²å¤–ï¼Œç»„ä»¶è¿˜æä¾›äº†å…¶ä»–è‰²ç³»çš„æ ‡ç­¾
 *    -- æ³¨æ„ï¼Œ`type` å±æ€§å°†å¼ƒç”¨ï¼Œ`color` å±æ€§åŒæ ·æ”¯æŒ type åŒå±æ€§å€¼çš„æ ·å¼
 * en - Color
 *    -- In addition to the five basic functions of the commonly used tag, the component also provides tags of other colors
 *    -- Note that the `type` property will be deprecated, and the `color` property also supports the style of the same property value as type
 */

import { TYPE, Tag } from 'shineout';

type TagColorType = Exclude<TYPE.Tag.Props['color'], undefined>
type TagModeType = Exclude<TYPE.Tag.Props['mode'], undefined>

export default () => {
  const TagColor: TagColorType[] = ['tangerine', 'magenta', 'purple', 'indigo', 'cyan', 'neon', 'lemon', 'brown'];
  const TagMode: TagModeType[] = ['bright', 'fill', 'outline', 'brightOutline'];

  const capitalizeFirstLetter = (str: string) => {
    return str.charAt(0).toUpperCase() + str.slice(1);
  };

  return (
    <div>
      {TagMode.map((mode, midx) => {
        return (
          <div key={midx} style={{ marginBottom: 24 }}>
            {TagColor.map((color, cidx) => (
              <Tag mode={mode} key={cidx} color={color}>
                {capitalizeFirstLetter(color)}
              </Tag>
            ))}
          </div>
        );
      })}
    </div>
  );
};

/**
 * cn - å¯åˆ é™¤å’Œæ·»åŠ æ ‡ç­¾
 *    -- é€šè¿‡æ•°ç»„ç”Ÿæˆtagsï¼ŒåŠ¨æ€å¢æ”¹
 * en - Delete and add tags
 *    -- Generate tags through arrays, dynamically increase and change
 */

import { useState } from 'react';
import { Tag } from 'shineout';
import { createUseStyles } from 'react-jss';

const useStyle = createUseStyles(
  {
    addTag: {
      '&:hover': {
        borderColor: 'var(--soui-tag-info-outline-border-color,var(--soui-brand-6,#197AFA))',
        color: 'var(--soui-tag-info-outline-font-color,var(--soui-brand-6,#197AFA))',
      },
    },
  },
  { name: 'custom-tag' },
);

const plusIcon = <svg viewBox="0 0 24 24" width="12px" height="12px" fill="currentColor" style={{display: 'block'}}>
  <path fillRule="evenodd" clipRule="evenodd" d="M11.8185 2.09595C12.3708 2.09595 12.8185 2.54366 12.8185 3.09595V10.8181H20.9041C21.4564 10.8181 21.9041 11.2658 21.9041 11.8181C21.9041 12.3704 21.4564 12.8181 20.9041 12.8181H12.8185V20.904C12.8185 21.4563 12.3708 21.904 11.8185 21.904C11.2662 21.904 10.8185 21.4563 10.8185 20.904V12.8181H3.10059C2.5483 12.8181 2.10059 12.3704 2.10059 11.8181C2.10059 11.2658 2.5483 10.8181 3.10059 10.8181H10.8185V3.09595C10.8185 2.54366 11.2662 2.09595 11.8185 2.09595Z"></path>
</svg>

export default () => {
  const [tags, setTags] = useState(['Tag 1', 'Tag 2', 'Tag 3']);
  const [inputVisible, setInputVisible] = useState(false);

  const classes = useStyle();

  const remove = (removedTag: string) => {
    const t = tags.filter((tag: string) => tag !== removedTag);
    setTags(t);
  };

  const showInput = () => {
    setInputVisible(true);
  };

  const handleInputBlur = (value: string) => {
    let newTags = tags;
    if (value && tags.indexOf(value) === -1) {
      newTags = [...tags, value];
    }
    setTags(newTags);
    setInputVisible(false);
  };

  return (
    <div>
      <div style={{ display: 'flex', alignItems: 'center', height: 24 }}>
        {tags.map((a) => (
          <Tag key={a} disabled={a === 'Tag 3' ? true : false} onClose={() => remove(a)}>
            {a}
          </Tag>
        ))}

        {inputVisible ? (
          <Tag.Input style={{ marginLeft: 8 }} onBlur={handleInputBlur} size='small' />
        ) : (
          <Tag
            style={{ cursor: 'pointer', borderStyle: 'dashed' }}
            onClick={showInput}
            mode='outline'
            className={classes.addTag}
          >
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <div style={{ marginInlineEnd: 4 }}>{plusIcon}</div>
              <div>Add Tag</div>
            </div>
          </Tag>
        )}
      </div>
    </div>
  );
};

