/**
 * cn - 基本用法
 *    -- 基础的徽标展示。当 `count` 为0时默认不展示，可以通过配置 `showZero` 修改为展示。
 * en - Basic
 *    -- The basic badge display, the badge is not displayed by default when `count` is 0. You can modify it to display by configuring `showZero`
 */
import React, { useState } from 'react';
import { Badge, Switch } from 'shineout';
import Avatar from './static/avatar';

export default () => {
  const [showZero, setShowZero] = useState(false);
  return (
    <div style={{ display: 'flex', alignItems: 'center', gap: 32 }}>
      <Badge count={1}>
        <Avatar></Avatar>
      </Badge>
      <Badge count={0} showZero={showZero}>
        <Switch onChange={setShowZero} value={showZero}></Switch>
      </Badge>
    </div>
  );
};

/**
 * cn - 独立使用
 *    -- 不包裹任何元素即是独立使用，可自定样式展现
 * en - No children
 *    -- Independent use, no wrapping elements is independent use, you can customize the style
 */
import React from 'react';
import { Badge } from 'shineout';

export default () => {
  return (
    <div style={{ display: 'flex', alignItems: 'center', gap: 42 }}>
      <Badge count={1}></Badge>
      <Badge count={100} overflowCount={99} color='#faad14'></Badge>
    </div>
  );
};

/**
 * cn - 封顶数字
 *    -- 设置`overflowCount`属性，当数字大于该值时显示 $\{overflowCount\}+
 * en - Overflow count
 *    -- Set the `overflowCount` property, when the number is greater than this value, display $\{overflowCount\}+
 */
import React from 'react';
import { Badge } from 'shineout';
import Avatar from './static/avatar';

export default () => {
  return (
    <div style={{ display: 'flex', alignItems: 'center', gap: 32 }}>
      <Badge count={99}>
        <Avatar></Avatar>
      </Badge>
      <Badge count={100} overflowCount={9}>
        <Avatar></Avatar>
      </Badge>
      <Badge count={100} overflowCount={99}>
        <Avatar></Avatar>
      </Badge>
      <Badge count={1000} overflowCount={999}>
        <Avatar></Avatar>
      </Badge>
    </div>
  );
};

/**
 * cn - 尺寸
 *    -- 设置 `size` 属性为 `small` 可以显示小尺寸的徽标。支持的尺寸有 `small` 和 `default`。
 * en - Size
 *    -- Set the `size` property to `small` to display a small badge. The supported sizes are `small` and `default`.
 */
import React from 'react';
import { Badge } from 'shineout';
import Avatar from './static/avatar';

export default () => {
  return (
    <div style={{ display: 'flex', alignItems: 'center', gap: 24 }}>
      <Badge count={99} size='small'>
        <Avatar></Avatar>
      </Badge>
      <Badge count={99}>
        <Avatar></Avatar>
      </Badge>
    </div>
  );
};

/**
 * cn - 小点模式
 *    -- 开启 `dot` 小点模式，不展示数字
 * en - Dot
 *    -- Open `dot` dot mode, do not display numbers
 */
import React from 'react';
import { Badge } from 'shineout';
import Avatar from './static/avatar';

export default () => {
  return (
    <div style={{ display: 'flex', alignItems: 'center', gap: 32 }}>
      <Badge dot>
        <Avatar></Avatar>
      </Badge>
    </div>
  );
};

/**
 * cn - 带状态的小点
 *    -- 配置`status`更改徽标的状态
 * en - Basic
 *    -- The basic badge display, the badge is not displayed by default when `count` is 0. You can modify it to display by configuring `showZero`
 */
import React from 'react';
import { Badge } from 'shineout';

export default () => {
  return (
    <div>
      <div style={{ display: 'flex', alignItems: 'center', gap: 24, marginBottom: 32 }}>
        <Badge dot status='default'></Badge>
        <Badge dot status='processing'></Badge>
        <Badge dot status='error'></Badge>
        <Badge dot status='warning'></Badge>
        <Badge dot status='success'></Badge>
      </div>
      <div style={{ display: 'flex', alignItems: 'center', gap: 24 }}>
        <Badge dot status='default' text='Default'></Badge>
        <Badge dot status='processing' text='Processing'></Badge>
        <Badge dot status='error' text='Error'></Badge>
        <Badge dot status='warning' text='Warning'></Badge>
        <Badge dot status='success' text='Success'></Badge>
      </div>
    </div>
  );
};
