/**
 * cn - 基本用法
 *    --基础用法
 * en - Base
 *    --Basic usage of `Textarea`
 */
import { Textarea } from 'shineout';

export default () => {
  return <Textarea autoFocus placeholder='input something' />;
};

/**
 * cn - 拖动高度
 *    -- 设置 `resize` 属性可以拖动改变高度
 * en - resize
 *    -- Set the `resize` property to change the height by dragging
 */
import { Textarea } from 'shineout';

export default () => {
  return <Textarea placeholder='input something' resize />;
};

/**
 * cn - 尺寸
 *    -- 提供了三种尺寸 `small | default | large`
 * en - Size
 *    -- There are three sizes: small, default, and large
 */
import React from 'react';
import { Textarea } from 'shineout';

const style: React.CSSProperties = { width: 120, marginInlineEnd: 24 };

const App: React.FC = () => (
  <div>
    <Textarea size='small' rows={3} style={style} placeholder='small size' />
    <Textarea style={style} rows={3} placeholder='default size' />
    <Textarea size='large' rows={3} style={style} placeholder='large size' />
  </div>
);

export default App;

/**
 * cn - 自适应高度
 *    -- `autosize` 为 true 时， rows 为最小高度，如果要设置最大高度，使用 `maxHeight` 即可
 * en - Adaptive height
 *    -- `autosize` 为 true 时， rows 为最小高度，如果要设置最大高度，使用 `maxHeight` 即可
 */
import { Textarea } from 'shineout';

export default () => {
  return (
    <Textarea
      autosize
      rows={2}
      placeholder='Please enter content, the height can be automatically adjusted'
      maxHeight={150}
      resize
    />
  );
};

/**
 * cn - 提示信息
 *    -- 设置 `info` 为数字, 设定最大长度，用户 focus 时会显示用户已输入文字长度。
 *    -- 如果超出长度， 则会报错，不会隐藏。
 * en - Info
 *    -- Set info to number, set the maximum length, and the user's focus shows the length of text that the user has entered
 *    -- If the length is exceeded, the error is reported. It is not hidden
 *
 */
import React from 'react';
import { Textarea } from 'shineout';

const App: React.FC = () => <Textarea rows={4} trim placeholder='input something' info={10} />;

export default App;

/**
 * cn -
 *    -- 可以通过设置 `info` 为函数去自定义提示信息;
 *    -- 如果 `info` 返回类型为 Error，不会隐藏；
 *    -- 可以通过设置 `popoverProps` 来自定义弹窗的属性， 参考Popover组件的属性。
 * en -
 *    -- can customize the info by setting info as a function;
 *    -- if the functio return an Error , the info doesn't hide;
 *    -- can customize the popover by setting `popoverProps` , reference to Popover component's props.
 */
import React from 'react';
import { Textarea } from 'shineout';

const renderInfo = (value?: string) => {
  if (!value || value.length === 0) return null;
  const text = `total is  ${value.length}`;
  if (value.length <= 20) return text;
  return new Error(text);
};

const App: React.FC = () => (
  <Textarea
    rows={4}
    trim
    placeholder='input something'
    info={renderInfo}
    popoverProps={{
      position: 'bottom-left',
      getPopupContainer: () => document.body,
    }}
  />
);

export default App;

/**
 * cn - 字数统计
 *    -- 设置 `limit` 为数字，右下角显示字数统计
 *    -- 输入达到最大字数后，不再允许输入
 * en - Limit
 *    -- Set the `limit` property to display the word count
 */
import React from 'react';
import { Textarea } from 'shineout';

const App: React.FC = () => (
  <Textarea rows={3} limit={50} placeholder='input something' />
);

export default App;

/**
 * cn -
 *    -- 设置 `limit` 为函数，可以自定义右下角的字数统计显示
 *    -- 输入达到最大字数后，可以继续输入
 * en -
 *    -- Set the `limit` to a function to customize the display of the word count
 */
import React from 'react';
import { Textarea } from 'shineout';

const max = 100
const App: React.FC = () => {
  const [value, setValue] = React.useState('');

  const error = value.length > max ? 'error' : undefined;
  return (
    <Textarea
      status={error}
      limit={(text) => (
        <span>
          最大字数：
          <span
            style={{
              color: error ? 'var(--soui-danger-6,#EB4242)' : 'var(--soui-brand-6,#197AFA)',
            }}
          >
            {text?.length || 0}
          </span> / {max}
        </span>
      )}
      value={value}
      onChange={setValue}
      placeholder='input something'
    />
  );
};

export default App;

/**
 * cn - 底部信息
 *    -- 设置 `renderFooter` 可自定义底部信息
 * en - RenderFooter
 *    -- render textarea `footer`
 */
import React from 'react';
import { Textarea } from 'shineout';

function renderFooter(text: string = '') {
  const style: React.CSSProperties = {
    color: text.length > 20 ? 'var(--danger-6,#CC3D3A)' : 'inherit',
  };
  return (
    <div style={{ textAlign: 'end', color: 'var(--neutral-text-2, #b3b7c1)' }}>
      <span style={style}>{text.length}</span> / 20
    </div>
  );
}

const App: React.FC = () => (
  <Textarea rows={3} renderFooter={renderFooter} placeholder='input something' />
);

export default App;

/**
 * cn - 可清除
 *    -- 设置 `clearable` 可以显示清除按钮
 * en - Clearable
 *    -- Set `clearable` to show clear button
 */
import React from 'react';
import { Textarea } from 'shineout';

const App: React.FC = () => (
  <Textarea placeholder='input something' clearable />
);

export default App;

/**
 * cn - Rules
 *   --
 * en - Rules
 *    --
 */
import { Rule, Textarea } from 'shineout';

const rule = Rule();
export default () => {
  return (
    <Textarea
      rules={[rule.required('必填')]}
      tip={'input something'}
      placeholder='input something'
    />
  );
};

/**
 * cn - innerTitle
 *   --
 * en - innerTitle
 *    --
 */
import { Rule, Textarea } from 'shineout';

const rule = Rule();
export default () => {
  return (
    <Textarea
      rows={1}
      innerTitle={'hello world'}
      rules={[rule.required('必填')]}
      placeholder='input something'
    />
  );
};
