/**
 * cn - 节点点击行为
 *    -- 设置 `actionOnClick` 属性可以在点击节点时触发相应的行为
 *    -- 属性说明：`check` 选中复选框，`expand` 展开/收起节点
 * en - Action on click
 *    -- Set the `actionOnClick` property to trigger the corresponding behavior when clicking the node
 *    -- Property description: `check` check the checkbox, `expand` expand/collapse the node
 */
import React, { useState } from 'react';
import { TreeSelect, Form, Checkbox, TYPE } from 'shineout';

type TreeSelectProps = TYPE.TreeSelect.Props<DataItem, string>;

interface DataItem {
  id: string;
  title: string;
  children?: DataItem[];
}

const data: DataItem[] = [
  {
    id: '1',
    title: '1',
    children: [
      {
        id: '1-1',
        title: '1-1',
        children: [
          { id: '1-1-1', title: '1-1-1' },
          { id: '1-1-2', title: '1-1-2' },
        ],
      },
      { id: '1-2', title: '1-2' },
    ],
  },
  {
    id: '2',
    title: '2',
    children: [
      { id: '2-1', title: '2-1' },
      { id: '2-2', title: '2-2' },
    ],
  },
  { id: '3', title: '3', children: [{ id: '3-1', title: '3-1' }] },
];

export default () => {
  const [value, setValue] = useState<TreeSelectProps['value']>();
  const [config, setConfig] = useState<{ actionOnClick: TreeSelectProps['actionOnClick'] }>({
      actionOnClick: ['check'],
    });

  const handleChange: TreeSelectProps['onChange'] = (v) => {
    setValue(v);
  };

  return (
    <>
      <Form value={config} onChange={setConfig}>
        <Form.Item label='配置点击行为:' labelWidth='7em' labelAlign='left'>
          <Checkbox.Group name='actionOnClick' keygen data={['check', 'expand']} />
        </Form.Item>
      </Form>
      <TreeSelect
        keygen='id'
        multiple
        value={value}
        onChange={handleChange}
        width={300}
        data={data}
        compressed
        renderItem={(node) => `node ${node.title}`}
        clearable
        actionOnClick={config.actionOnClick}
        placeholder='Please select content'
      />
    </>
  );
};

/**
 * cn - 虚拟列表
 *    -- 设置 `virtual` 属性，开启虚拟列表功能，本例为 100000 条数据
 * en - Virtual list
 *    -- Set the `virtual` property to enable the virtual list function, this example has 100000 data
 */
import React, { useState } from 'react';
import { TreeSelect, TYPE } from 'shineout';
import { createNestedArray } from '../../tree/__example__/utils';

type TreeSelectProps = TYPE.TreeSelect.Props<DataItem, string>;

interface DataItem {
  id: string;
  children?: DataItem[];
}

const data: DataItem[] = createNestedArray([10, 10, 10]);

export default () => {
  const [value, setValue] = useState<TreeSelectProps['value']>();

  const handleChange: TreeSelectProps['onChange'] = (v) => {
    console.log(v);
    setValue(v);
  };

  const handleFilter = (text: string) => (d: DataItem) => d.id === text;

  return (
    <div style={{ width: '100%', display: 'flex', alignItems: 'center', gap: 24 }}>
      <TreeSelect
        width={300}
        value={value}
        mode={2}
        multiple
        onChange={handleChange}
        clearable
        onFilter={handleFilter}
        virtual
        keygen='id'
        renderItem={(node) => `node ${node.id}`}
        data={data}
        placeholder='Please select content'
      ></TreeSelect>
    </div>
  );
};

